import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { UserDashboardService } from 'services/user-dashboard.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  constructor(private router: Router, private userDashboardService: UserDashboardService) { }
  error: boolean = false;
  isActive: boolean = false;
  ngOnInit(): void {
  }

  onSubmit(formData: any) {
    console.log(formData.form.value.email)
    console.log(formData.form.value.password)
    if (formData.value.email == "" || formData.value.password == "") {
      this.error = true;
    } else {
      this.userDashboardService.login(formData.form.value).subscribe((response: { error: string; }) => {
        if (response.error == "error") {
          this.router.navigateByUrl('/')
        } else {
          this.router.navigateByUrl('/dashboard')
        }
      })
    }
  }

  changeActive(){
    this.isActive = !this.isActive;
  }

}
